import React, { useState } from 'react';


const Accueil = () => {
    // Définir les états pour les champs du formulaire
    const [name, setName] = useState('');
    const [capacity, setCapacity] = useState('');

  
    const handleSubmit = (event) => {
        event.preventDefault();

        const data = {
            name: name,
            capacity: capacity
        };

        axios.post('http://localhost:8000/login/', data)
        .then((response) => {
         if(response.data.message){
            window.location.href = "/Accueil";
         }else{
            console.error(response.data.error);
         }

        })
        .catch((error) => {
            console.error(error);
        });
       
    return (
        <>
            <div>
                <h1>Accueil</h1>
                <form onSubmit={handleSubmit}>
                    <label htmlFor="name">Nom de la room</label>
                    <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} />
                    <br />
                    <label htmlFor="number">Capacité</label>
                    <input type="number" id="number" value={capacity} onChange={(e) => setCapacity(e.target.value)} />
                    <br />
                    <button type="submit">Ajouter</button>
                </form>
            </div>
        </>
    );
};
}

export default Accueil;
